        }]
    }],
    imagePaths: [],
    postAdded: false,
}

const ADD_POST = 'ADD_POST';
export const addPost = {
    type: ADD_POST,
}

const dummyPost = {
    id: 2,
    content: '더미 데이터지롱',
    User: {
        id: 2,
        nickname: 'Cookie'
    },
    Images: [],
    Comments: [],
}

const reducer = (state = initialState ,action) => {
    switch (action.type) {
        case ADD_POST:
            return {
                ...state,
                mainPosts: [dummyPost, ...mainPosts],
                postAdded: true,
            }
        default:
            return state;
    }
}

export default reducer;
