const initialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginDaga: {},
    },
    post: {
        mainPosts: [],
    }
};

export const loginAction = (data) => {
    return {
        type: 'LOG_IN',
        data,
    }
}
// //action creator
